(75, 1505213864, 3, 'Медосмотр', '<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNUlBY1QwTWNpS2c" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNUlBY1QwTWNpS2c" style="height:425px; width:300px" /></a></td>\r\n			<td>\r\n			<p><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOUdiRXNMdi1GY0U" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOUdiRXNMdi1GY0U" style="height:211px; width:300px" /></a><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRTV3cWtnUENFcGc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRTV3cWtnUENFcGc" style="height:216px; width:300px" /></a></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><a href="https://drive.google.com/file/d/0B-SC8AHRnwOMRDUtMVBRZU16aFE/view?usp=sharing" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><strong>График проведения медосмотра студентов 1 курса ГБПОУ УКСИВТ</strong></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(78, 1505363645, 3, 'Работа со студентами', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTzVvUGVJSFlILWc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTzVvUGVJSFlILWc" style="height:225px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:justify"><span style="font-size:12.0pt"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">8 сентября для групп специальности Правоохранительная деятельность была прочитана лекция командиром отряда народной дружины &quot;правопорядок&quot; по Кировскому району, города Уфы Хамитовым Маратом Махмутовичем , где были затронуты вопросы об уголовной и административной ответственности среди. несовершеннолетних </span></span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTzVvUGVJSFlILWc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTzVvUGVJSFlILWc" style="height:225px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:justify"><span style="font-size:12.0pt"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">8 сентября для групп специальности Правоохранительная деятельность была прочитана лекция командиром отряда народной дружины &quot;правопорядок&quot; по Кировскому району, города Уфы Хамитовым Маратом Махмутовичем , где были затронуты вопросы об уголовной и административной ответственности среди. несовершеннолетних </span></span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:12.0pt"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Дополнение к выступлению внесла инспектор отдела профилактики правонарушений несовершеннолетних по Кировскому району Тимергалиева Луиза Канзафаровна&nbsp;<a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTV8zVlNpX1VzVHc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTV8zVlNpX1VzVHc" style="float:left; height:188px; width:250px" /></a></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"> , указав что, к сожалению, не каждый подросток, осознает о совершаемых им противоправных деяниях, которые ведут к тяжелым и трудно исправимым последствиям. Ежегодно подростками совершается более 145 тыс. преступлений, практически каждый пятый из них направляется для отбывания наказания в виде лишения свободы в воспитательные колонии.</span></span></p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMUtFQkp4eEdfMzg" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMUtFQkp4eEdfMzg" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQUUxVUtpQWNiSms" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQUUxVUtpQWNiSms" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSTNJYjJUX3JlYTA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSTNJYjJUX3JlYTA" style="height:150px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:12.0pt"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;Студенты получили разъяснение о </span></span></span><span style="font-size:12.0pt"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">профилактике преступлений и правонарушений среди несовершеннолетних, каждый получил представление об уголовной и административной ответственности за их совершение.</span></span></span></p>\r\n'),
(83, 1505477093, 3, 'Управление профессиональной организацией в условиях профстандартов', '<p style="text-align:justify">C 5 по 8 сентября 2017 года административно-управленческий персонал &nbsp;УКСИВТ прослушал цикл вебинаров на тему &quot;Управление профессиональной организацией в условиях профстандартов&quot;, организованный Академией профессионального развития. <img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYVdLVUw1eVgya00" style="float:left; height:200px; width:300px" />Целью цикла вебинаров является подготовка руководителей учреждений СПО к внедрению профессионального стандарта управления образовательной организацией в условиях национальной системы квалификаций ТОП-50.<br />\r\nНа вебинарах рассматривался отечественный и зарубежный опыт управления профессиональной образовательной организацией, перспективы и риски внедрения профстандарта &quot;Управление профессиональной организацией&quot;, проблемы при переходе на обучение по ТОП-50, вопросы внедрения новой системы стандартизации по ФГОС четвёртого поколения.Онлайн-мероприятия были проведены сотрудниками Центра профессионального образования и систем квалификаций ФГАУ &quot;ФИРО&quot;.</p>\r\n', '');
INSERT INTO `news` (`id`, `datetime`, `autor`, `title`, `preview`, `text`) VALUES
(84, 1505726864, 3, 'Первокурсник - 2017', '<p style="text-align:justify">14 сентября 2017 состоялся спортивный праздник &quot;Первокурсник - 2017&quot;. В празднике приняли участие студенты первых курсов с кураторами. Все участники получили море положительных эмоций. От всей души поздравляем первокурсников с началом учебного года! Желаем творческих успехов!</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTWJkQTdodzNmZ0E" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTWJkQTdodzNmZ0E" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdExKelVhNFFNOE0" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdExKelVhNFFNOE0" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRHBNbU1NTEJEYTA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRHBNbU1NTEJEYTA" style="height:133px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n', '<p style="text-align:justify">14 сентября 2017 состоялся спортивный праздник &quot;Первокурсник - 2017&quot;. В празднике приняли участие студенты первых курсов с кураторами. Все участники получили море положительных эмоций. От