(100, 1507026806, 3, 'Поздравляем', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMUkVRaGRoSUlZTW8" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMUkVRaGRoSUlZTW8" style="height:200px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify">Студент&nbsp; группы 3УКС-1 Саттаров Булат Ханифович занял 1 место в региональном отборочном этапе III Национального чемпионата профессионального мастерства &quot;Абилимпикс&quot; в компетенции&nbsp;&quot;Сетевое и системное администрирование&quot;. Поздравляем с победой!</p>\r\n\r\n			<p><img src="https://ssl.gstatic.com/ui/v1/icons/mail/images/cleardot.gif" /></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNUxHSTdvNnI3dGc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNUxHSTdvNnI3dGc" style="height:212px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMbzZOUjNtRFBIR0U" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMbzZOUjNtRFBIR0U" style="height:212px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMXy1JQXl2aFVCN0k" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMXy1JQXl2aFVCN0k" style="height:212px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMeFdabGRLaWhFaUk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMeFdabGRLaWhFaUk" style="height:212px; width:150px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(101, 1507181476, 3, '5 октября - День Учителя!', '<p><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYzhIOE5ydDZNcTg" style="height:544px; width:600px" /></p>\r\n', ''),
(102, 1507610868, 3, 'Благодарность', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMY2Z3QUdhZVktYzA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMY2Z3QUdhZVktYzA" style="height:212px; width:150px" /></a></td>\r\n			<td style="text-align:justify">Министерство образования Республики Башкортосан выразило благодарность Кунсбаеву Сайфутдину Зайнетдиновичу за активное участие в подготовке и проведении Республиканского конкурса профессионального мастерства в системе среднего профессионального образования &quot;Лучший преподаватель года - 2017&quot;.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(103, 1507611545, 3, 'Участие в семинаре', '<p style="text-align:justify">3 октября директор Уфимского колледжа статистики, информатики и вычислительной техники вместе с зам.директором по учебно-производственной работе приняли участие в семинаре-совещании на тему: &quot;Роль и место дополнительного профессионального образования в развитии непрерывного образования и национальных систем квалификаций&quot;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMU0VraUttNnlpY28" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMU0VraUttNnlpY28" style="height:212px; width:300px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYWtFS0xaN21LWU0" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="https://drive.google.com/open?id=0B-SC8AHRnwOMYWtFS0xaN21LWU0" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYWtFS0xaN21LWU0" style="height:212px; width:300px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><img src="https://ssl.gstatic.com/ui/v1/icons/mail/images/cleardot.gif" /></p>\r\n', '');
INSERT INTO `news` (`id`, `datetime`, `autor`, `title`, `preview`, `text`) VALUES
(104, 1507612484, 3, 'Коллектив колледжа совершили выезд по грибы', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSk1PcXlLNTJTOUU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSk1PcXlLNTJTOUU" style="height:200px; width:150px" /></a></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">8 октября сотрудники Уфимского колледжа статистики, информатики и вычислительной техники в честь празднования &quot;Дня Учителя&quot; организовали выезд в Чишминский район за грибами. Хорошая погода позволила участникам выезда не только собрать последние осенние грибы, но и насладиться осенней природой, лесом и свежим воздухом.</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT1VtakdQQ3JERVE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT1VtakdQQ3JERVE" style="height:150px; width:200px" /></a></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMM1E0WUh3ZElkVTA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMM1E0WUh3ZElkVTA" style="height:150px; width:200px" /></a></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOEpCOHl5dnJUcEk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOEpCOHl5dnJUcEk" style="height:150px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n', ''),
(105, 1507629960, 3, 'В Базовой профессиональной образовательной организации состоялся учебно-методический вебинар', '<p style="text-align:justify">С целью оказания методической помощи в разработке и внедрении адаптированной профессиональной образовательной программы по специальности, реализуемой в профессиональной образовательной организации, 06 октября 2017 года&nbsp; в Базовой профессиональной образовательной организации, обеспечивающей поддержку инклюзивного профессионального образования - ГБПОУ Уфимский колледж статистики, информатики и вычислительной техники состоялся вебинар для профессиональных образовательных организаций Республики Башкортостан и партнеров БПОО в других регионах Российской Федерации.</p>\r\n\r\n<p style="text-align:center"><iframe frameborder="0" height="315" src="https://www.youtube.com/embed/jD0a2fpdDZQ?list=PLC6bHDIjfVmHsIU4yc_2nZz1MvI0Dq_aG" width="560"></iframe></p>\r\n', '<p style="text-align:justify">С целью оказания методической помощи в разработке и внедрении адаптированной профессиональной образовательной программы по специальности, реализуемой в профессиональной образовательной организации, 06 октября 2017 года&nbsp; в Базовой профессиональной образовательной организации, обеспечивающей поддержку инклюзивного профессионального образования - ГБПОУ Уфимский колледж статистики, информатики и вычислительной техники состоялся вебинар для профессиональных образовательных организаций Республики Башкортостан и партнеров БПОО в других регионах Российской Федерации.</p>\r\n\r\n<p style="text-align:center"><iframe frameborder="0" height="315" src="https://www.youtube.com/embed/jD0a2fpdDZQ?list=PLC6bHDIjfVmHsIU4yc_2nZz1MvI0Dq_aG" width="560"></iframe></p>\r\n\r\n<p style="text-align:justify">В программе вебинара&nbsp; были рассмотрены следующие вопросы:</p>\r\n\r\n<p style="text-align:justify">1.Основные ориентиры государственной политики в сфере инклюзивного профессионального образования. (Докладчик: Ганеева Эльза Саляховна, старший преподаватель кафедры коррекционной педагогики ГАУ ДПО Институт развития образования Республики Башкортостан;)</p>\r\n\r\n<p style="text-align:justify">2.Структура адаптированных профессиональных образовательных программ для лиц с ОВЗ и инвалидностью. (Докладчик: Гареева Светлана Мияссаровна, к.и.н., заместитель директора по учебно-методической работе ГБПОУ Уфимский колледж статистики, информатики и вычислительной техники;)</p>\r\n\r\n<p style="text-align:justify">3.Адаптационный учебный цикл. (Докладчик: Абуталипова Эльза Насибулловна, к.псих.н., доцент, зав. кафедрой коррекционной педагогики ГАУ ДПО Институт развития образования.)</p>\r\n'),
(106, 1507810412, 3, 'Расписание аудиторных занятий установочной сессии заочной формы обучения', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:justify">\r\n			<p style="text-align:center"><span style="color:#e74c3c"><strong>ВНИМАНИЕ!</strong></span></p>\r\n\r\n			<p>На сайте ГБПОУ УКСИВТ опубликовано&nbsp;<a href="https://drive.google.com/open?id=0B-SC8AHRnwOMN21oYVJ0X0VGcEE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий установочной сессии заочной формы обучения отделения информатиик и вычислительной техники&nbsp; для групп 1ИС-17, 2ИС9-16</a></p>\r\n\r\n			<p><span style="color:#e74c3c">Сроки проведения с 16 октября 2017 года по 21 октября 2017 года.</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(107, 1508148699, 1, 'Вниманию студентов выпускных групп отделения информатики', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMVDJHNWFrWEVPejA" style="float:left; height:91px; width:200px" />В целях организации прохождения производственной практики, студентам выпускных групп необходимо заполнить следующую таблицу до 25 октября:</p>\r\n\r\n<p style="text-align:justify"><a href="https://docs.google.com/forms/d/e/1FAIpQLSfYiWllCwhllm9j_fYdAyCgjW_vsk1rcJFDSd6wJKBXW8hi0g/viewform">Форма сбора данных о базе прохождения практики</a></p>\r\n\r\n<p style="text-align:justify">Студенты, не заполнившие форму до 25 октября будут распределены по базам прохождения практики, без возможности её смены.</p>\r\n\r\n<p style="text-align:justify">Образцы документов для прохождения практики доступны по следующей ссылке:</p>\r\n\r\n<p style="text-align:justify"><a href="https://drive.google.com/open?id=0B63BVNhQiF4mZU84TUhSc0xHeVU">https://drive.google.com/open?id=0B63BVNhQiF4mZU84TUhSc0xHeVU</a></p>\r\n\r\n<p style="text-align:justify">1 ноября старостам групп, необходимо подойти в 7 кабинет (Лаборатория проектной деятельности и инновационных информационных систем) для централизованного получения договоров на прохождение практики.</p>\r\n', ''),
(108, 1508161200, 3, 'ГОРЯЧАЯ ЛИНИЯ БПОО +7(347)291-22-54', '<p style="text-align:center">&nbsp;</p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><a href="http://uksivt.ru/?r=news/select&amp;id=108" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMY1JiSjhIQ2xDNlU" style="height:150px; width:150px" /></a></td>\r\n			<td style="text-align:center">&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:center">В Базовой профессиональной образовательной организации, обеспечивающей поддержку региональной системы инклюзивного профессионального образования&nbsp; -&nbsp;ГБПОУ Уфимский колледж статистики, информатики и вычислительной техники&nbsp; начинает работу</p>\r\n\r\n			<p style="text-align:center"><a href="http://uksivt.ru/?r=news/select&amp;id=108" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><span style="color:#e74c3c"><u><strong>ГОРЯЧАЯ ЛИНИЯ</strong></u></span></a></p>\r\n\r\n			<p style="text-align:center"><a href="http://uksivt.ru/?r=news/select&amp;id=108"><span style="color:#e74c3c"><strong><u>для оперативного консультирования:</u></strong></span></a></p>\r\n			</td>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:center"><a href="uksivt.ru/?r=news/select&amp;id=108" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMb19fQkNxYjJhcXM" style="height:150px; width:150px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><a href="http://uksivt.ru/?r=news/select&amp;id=108" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMVm1rdVZtY1B0WFE" style="height:109px; width:286px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<p style="text-align:center">В Базовой профессиональной образовательной организации, обеспечивающей поддержку региональной системы инклюзивного профессионального образования&nbsp; -&nbsp;ГБПОУ Уфимский колледж статистики, информатики и вычислительной техники&nbsp;начинает работу&nbsp;</p>\r\n\r\n<p style="text-align:center"><span style="color:#e74c3c"><u><strong>ГОРЯЧАЯ ЛИНИЯ</strong></u></span></p>\r\n\r\n<p style="text-align:center"><span style="color:#e74c3c"><u><strong>для оперативного консультирования:</strong></u></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- <span style="color:#e74c3c">ПОТЕНЦИАЛЬНЫХ АБИТУРИЕНТОВ</span> &ndash; инвалидов и лиц с ограниченными возможностями здоровья (ОВЗ) и ИХ РОДИТЕЛЕЙ (ЗАКОНННЫХ ПРЕДСТАВИТЕЛЕЙ) по вопросам получения инклюзивного профессионального образования в Республике Башкортостан;</p>\r\n\r\n<p>- <span style="color:#e74c3c">ОБУЧАЮЩИХСЯ</span> из числа инвалидов и лиц с ОВЗ по любым вопросам, возникающим в процессе получения инклюзивного профессионального образования в Республике Башкортостан;</p>\r\n\r\n<p>- <span style="color:#e74c3c">ВЫПУСКНИКОВ</span> &nbsp;&nbsp;из числа инвалидов и лиц с ОВЗ по вопросам дальнейшего трудоустройства (занятости).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>ТЕЛЕФОН ГОРЯЧЕЙ ЛИНИИ:&nbsp;<strong><span style="color:#e74c3c">+7(347)291-22-54</span></strong></p>\r\n\r\n<p>SKYPE:&nbsp;<strong><span style="color:#e74c3c">bpoo hotline</span></strong></p>\r\n\r\n<p>E-MAIL:&nbsp;<strong><a href="mailto:hotlinbpoo@mail.ru"><span style="color:#e74c3c">hotlinbpoo@mail.ru</span></a></strong></p>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n'),
(109, 1508238839, 3, 'Юнармейцы местного отделения ВВПОД «Юнармия» УКСИВТ посетили воинскую часть №63494', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=109" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdTB5eVVUQm9uWGc" style="float:left; height:199px; width:200px" /></a>На территории 12-й отдельной гвардейской краснознаменной Кенигсбергско-Городокской инженерной бригады, которая располагается в селе Алкино-2 Чишминского района Башкирии, 14 октября прошел День призывника.<br />\r\n<br />\r\nВ экскурсии по территории инженерной бригады приняли участие призывники из Ленинского, Кировского и Орджоникидзевского районов Уфы, Чишминского района республики, а также участники движения местного отделения ВВПОД &laquo;Юнармия&raquo; УКСИВТ. Гостям показали столовую, общежитие для личного состава, кинологический питомник, где собак обучают распознавать взрывчатые вещества и парк военной техники.<br />\r\n&nbsp;</p>\r\n', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdTB5eVVUQm9uWGc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdTB5eVVUQm9uWGc" style="float:left; height:199px; width:200px" /></a>На территории 12-й отдельной гвардейской краснознаменной Кенигсбергско-Городокской инженерной бригады, которая располагается в селе Алкино-2 Чишминского района Башкирии, 14 октября прошел День призывника.<br />\r\n<br />\r\nВ экскурсии по территории инженерной бригады приняли участие призывники из Ленинского, Кировского и Орджоникидзевского районов Уфы, Чишминского района республики, а также участники движения местного отделения ВВПОД &laquo;Юнармия&raquo; УКСИВТ. Гостям показали столовую, общежитие для личного состава, кинологический питомник, где собак обучают распознавать взрывчатые вещества и парк военной техники.<br />\r\n<br />\r\nПризывникам в торжественной обстановке выдали повестки &mdash; им предстоит отправиться в ряды Вооруженных Сил уже 25 октября.<br />\r\n<br />\r\n<a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMb25aaVhkUTAzMFE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMb25aaVhkUTAzMFE" style="float:right; height:188px; width:250px" /></a>&laquo;В этом году отправятся служить более 5300 человек. Это меньше, чем в прошлом году, &mdash; говорит Андрей Пронин. &mdash; В настоящее время осуществляется переход на контрактную службу, постепенно призыв &laquo;срочников&raquo; сокращается. Кроме того, свои коррективы вносит и сложная демографическая ситуация. Ребята отправятся и в западные районы России и на Дальний Восток. Работа с призывниками оказывает воздействие &mdash; всё чаще хотят служить, причем мечтают попасть на службу в Военно-морской флот, воздушно-десантные войска, морскую пехоту. По возможности, в выборе помогаем, но здесь ключевым критерием отбора становится состояние здоровья&raquo;.<br />\r\n<br />\r\n<a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOVl5bWozekh2cGM" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOVl5bWozekh2cGM" style="float:left; height:188px; width:250px" /></a>1 октября в России начался призыв граждан 1990-1999 годов рождения на военную службу. В этом году в ряды Вооруженных Сил из Башкортостана отправятся более 5300 человек. Из них в Военно-космические силы &mdash; 260 человек, Ракетные войска стратегического назначения &mdash; 180, ВДВ &mdash; 80, воинские части центрального подчинения &mdash; 250, войска Национальной гвардии России &mdash; 300.<br />\r\n<br />\r\nВсе призывники получают военную форму одежды на сборном пункте &mdash; она выдается по родам и видам войск.</p>\r\n'),
(110, 1508247970, 3, 'встреча кураторов 1-х курсов', '<p>&nbsp;</p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify">16 и 17 октября состоялась рабочая встреча кураторов 1-х курсов и преподавателей-предметников.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNHRDX0RsaDl5YTQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNHRDX0RsaDl5YTQ" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRW96VmlQXzRnV1U" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRW96VmlQXzRnV1U" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYnFTbFluMXhtQ2M" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYnFTbFluMXhtQ2M" style="height:150px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(111, 1508248346, 3, '85 лет  ГБПОУ УКСИВТ', '<p style="text-align:justify">15 октября 2017 года&nbsp; нашему колледжу исполнилось 85 лет! В честь юбилея преподавателями колледжа была совершена поездка в красивейшее место, на гору Нарыстау, которая находится в Миякинском районе Республики Башкортостан. Это святое&nbsp;место привлекает паломников из многих регионов и стран. История этого памятника старинная и окружена легендами. Исторические события этого периода отражены в однои из башкирских эпосов под названием &laquo;Идукай и Мурадым&raquo;, который на сегодняшний день признан в качестве литературного памятника.&nbsp;С подножия горы стекает родник, вода которого имеет целебные свойства.&nbsp;Независимо от того, правдивые все эти истории или нет, трудно не согласиться с тем, насколько красива гора Нарыстау. Святой источник также помогает многим людям своей целебной силой.&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdllfR2k5NU04RUE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdllfR2k5NU04RUE" style="height:113px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQXAzSkpiVFg1c00" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQXAzSkpiVFg1c00" style="height:113px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTG94bXhqZHByZ00" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTG94bXhqZHByZ00" style="height:113px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(112, 1508316273, 3, 'Расписание аудиторных занятий установочной сессии заочной формы обучения', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:center"><strong>ВНИМАНИЕ!</strong></p>\r\n\r\n			<p style="text-align:justify">На сайте ГБПОУ УКСИВТ опубликовано&nbsp;<a href="https://drive.google.com/open?id=0B-SC8AHRnwOMQ01ZbGpmUFFJMEE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий установочной сессии заочной формы обучения отделения экономики, права и земельно-имущественных отношений для групп 1ПСО С-17, 1ЗИО С-17</a></p>\r\n\r\n			<p style="text-align:justify">Сроки проведения с 23 октября 2017 года по 27 октября 2017 года.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(113, 1508399603, 3, 'В Башкортостане впервые пройдет Уфимский международный   салон образования', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://uksivt.ru/?r=news/select&amp;id=113" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMFFHSzREQkE4V3M" style="float:left; height:206px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify">23-26 октября текущего года в Уфе на площадке ВК &laquo;ВДНХ-ЭКСПО&raquo; впервые состоится Уфимский международный салон образования &ndash; 2017 &laquo;Образование будущего&raquo; (далее &ndash; УМСО-2017&laquo;Образование будущего&raquo;) Мероприятие организовано Министерством образования Республики Башкортостан и Московским международным салоном образования (ММСО), в числе соорганизаторов также выступает Министерство семьи, труда и социальной защиты населения Республики Башкортостан.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMFFHSzREQkE4V3M" style="float:left; height:138px; width:200px" />23-26 октября текущего года в Уфе на площадке ВК &laquo;ВДНХ-ЭКСПО&raquo; впервые состоится Уфимский международный салон образования &ndash; 2017 &laquo;Образование будущего&raquo; (далее &ndash; УМСО-2017&laquo;Образование будущего&raquo;) Мероприятие организовано Министерством образования Республики Башкортостан и Московским международным салоном образования (ММСО), в числе соорганизаторов также выступает Министерство семьи, труда и социальной защиты населения Республики Башкортостан.</p>\r\n\r\n<p style="text-align:justify">УМСО-2017 &laquo;Образование будущего&raquo; &ndash; это международная дискуссионная площадка и выставка современных образовательных технологий и инновационных проектов, лучших инфраструктурных и интеллектуальных решений.</p>\r\n\r\n<p style="text-align:justify">23-25 октября 2017 года школьники смогут проконсультироваться<br />\r\nу специалистов приемных комиссий образовательных организаций высшего и профессионального образования &nbsp;о приемной кампании 2018 &ndash; 2019 учебного&nbsp; года.</p>\r\n\r\n<p style="text-align:justify">В масштабной деловой программе УМСО-2017 &laquo;Образование будущего&raquo; обсуждение современных трендов и совместная генерация перспективных идей развития образования страны и региона, трансляция лучших российских и международных практик в сфере дошкольного, общего и высшего образования профессиональному сообществу России.</p>\r\n\r\n<p style="text-align:justify">Руководители и специалисты образовательных организаций, представители органов управления образованием и компаний - разработчиков образовательных технологий примут участие в мероприятии. &nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style="text-align:justify">УМСО-2017 &laquo;Образование будущего&raquo; станет первой регулярной региональной экспертной площадкой, организованной при поддержке и с использованием технологий организации и проведения Московского международного салона образования.</p>\r\n\r\n<p style="text-align:justify">26 октября 2017 года в последний день проведения УМСО-2017 &laquo;Образование будущего&raquo; на площадке ВК &laquo;ВДНХ-ЭКСПО&raquo; одновременно состоится республиканский фестиваль &laquo;PROFФЕСТ&raquo;, организованный Министерством семьи, труда и социальной защиты населения Республики Башкортостан. Школьники смогут ознакомиться с рабочими специальностями, востребованными на рынке труда, получить помощь в выборе будущей профессии, освоить профессиональные рабочие тренажеры, рассмотреть специальную технику, представленную ведущими предприятиями Республики Башкортостан.</p>\r\n\r\n<p style="text-align:justify">Чтобы посетить УМСО&ndash;2017 &laquo;Образование будущего&raquo; необходимо заполнить форму регистрации на <a href="http://www.mmco-expo.ru/ufa">сайте</a> форума. Участие в мероприятии бесплатное.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Организаторы:</p>\r\n\r\n<p style="text-align:justify">Министерство образования Республики Башкортостан; Московский международный салон образования; Министерство семьи, труда и социальной защиты населения Республики Башкортостан.</p>\r\n\r\n<p style="text-align:justify">Оператор мероприятия:</p>\r\n\r\n<p style="text-align:justify">Башкирская выставочная кампания</p>\r\n\r\n<p style="text-align:justify">+(347) 246-42-73, 246-42-75, e-mail: <a href="mailto:onk@bvkexpo.ru">onk@bvkexpo.ru</a>.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Время работы УМСО -2017: 23 &ndash; 25 октября 2017 , 10:00 &ndash; 18:00</p>\r\n\r\n<p style="text-align:justify">Открытие: 10.00 &nbsp;&nbsp;(время открытия)</p>\r\n\r\n<p style="text-align:justify">Пресс-служба: +73472180332 &nbsp;(контакты пресс-службы)</p>\r\n\r\n<p style="text-align:justify">Для участия в деловой программе УМСО-2017 с докладом необходимо подать заявку <a href="http://www.mmco-expo.ru/ufa" target="_blank">www.mmco-expo.ru/ufa</a></p>\r\n\r\n<p style="text-align:justify"><em>Справочно: Система образования Республики Башкортостан была успешно представлена на Московском международном салоне образования в апреле 2017 года, в котором приняли участие более 55 тысяч посетителей.</em></p>\r\n\r\n<p style="text-align:justify"><em>В соответствии с решением Организационного комитета по подготовке и проведению Московского международного салона образования от 26 апреля 2017 года №1 под председательством Заместителя Председателя Правительства Российской Федерации, председателя организационного комитета Голодец О.Ю. проработан вопрос о более широком вовлечении регионов в процессы выявления и передачи передового опыта.</em></p>\r\n\r\n<p style="text-align:justify"><em>В соответствии с предложением Министерства образования и науки Российской Федерации от 26 июня 2017 года № ВК-1674/09 Правительством Республики Башкортостан подписано распоряжение о проведении Уфимского международного салона образования &laquo;Образование будущего&raquo; (далее &ndash; УМСО-2017 &laquo;Образование будущего&raquo;) с использованием технологий организации и проведения Московского международного салона образования (в г.Уфа Республики Башкортостан), а также состав организационного комитета по подготовке и проведению УМСО-20178 г. &nbsp;&laquo;Образование будущего&raquo;.</em></p>\r\n'),
(114, 1508498494, 3, 'Встреча Героя России Ихсанова Р.Р. с молодежью', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMS2tWbFdQc1N2Ync" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMS2tWbFdQc1N2Ync" style="height:132px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMcW5sLUZMblVSQVU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMcW5sLUZMblVSQVU" style="height:132px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMd2xaRmM5RldHYUk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMd2xaRmM5RldHYUk" style="height:132px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify">\r\n			<p>20 октября 2017 года в Региональной общественной приемной Председателя Партии &quot;Единая Россия&quot; Д.А. Медведева прошла встреча Героя России, полковника ВВС в отставке Ихсанова Р.Р. и контрадмирала с представителями молодежных и студенческих организаций.</p>\r\n\r\n			<p>Данное мероприятие организовано в рамках проекта Партия &quot;Единая Россия&quot; &quot;Память и гордость в сердцах поколений&quot;, направленного на проведение единой государственной политики в области патриотического воспитания граждан Российской Федерации путем вовлечения молодежи в конкретную проектную и исследовательскую работу.</p>\r\n\r\n			<p>Студенты нашего колледжа приняли активное участие в данном мероприятии.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaGRGRVM3ZHJweUE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaGRGRVM3ZHJweUE" style="height:132px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaUdLbXdBYTRPd2c" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaUdLbXdBYTRPd2c" style="height:132px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMeTVtOTZ5Y1ZJdlE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMeTVtOTZ5Y1ZJdlE" style="height:132px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(115, 1508499436, 3, 'Расписание аудиторных занятий установочной сессии заочной формы обучения', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify"><strong>ВНИМАНИЕ!</strong></p>\r\n\r\n			<p style="text-align:justify">На сайте ГБПОУ УКСИВТ опубликовано&nbsp;<a href="https://drive.google.com/open?id=0B-SC8AHRnwOMd0ZxUVp6enI0dlU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий установочной сессии заочной формы обучения отделения экономики, права и земельно-имущественных отношений для групп 5ПСО С-15, 5ЗИО С-15</a></p>\r\n\r\n			<p style="text-align:justify">Сроки проведения с 30 октября 2017 года по 17 ноября 2017 года.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(116, 1508822077, 3, 'Открытый публичный Всероссийский смотр-конкурс образовательных организаций', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify">ГБПОУ УКСИВТ по итогам мероприятия &quot;Открытый публичный Всероссийский смотр-конкурс образовательных организаций&quot; проходившего в период с 23 января по 28 февраля 2017 г. вошло в число Лауреатов - Победителей и награжден грамотой Лауреата - Победителя за оригинальность оформления электронного стенда образовательного учреждения.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMYnJxTktuZWRsZFE" style="height:424px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOU1Hdm1TYlZtWms" style="height:424px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(117, 1508911342, 3, 'Благодарность', '<p style="text-align:center">&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOTZOa1ItTVZMdjA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOTZOa1ItTVZMdjA" style="height:424px; width:300px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(118, 1508932589, 3, 'Юниор-Лига КВН ', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMZXowbGJ5bFdKa3M" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMZXowbGJ5bFdKa3M" style="height:133px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOFJrcTBKcC15azQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOFJrcTBKcC15azQ" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMbHhXZXYzX1VYMmc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMbHhXZXYzX1VYMmc" style="height:127px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">14 октября в Нефтекамске прошел второй фестиваль Башкирской Юниор-Лиги КВН сезона 2017-2018, где впервые приняла участие команда &quot;УКСИВТ&quot; под руководством &nbsp;преподавателя английского языка Мухаметовой Л.В. и прошла в 1/4 финала, которая пройдет 9 декабря в Уфимском Конгресс-холле. Поздравляем наших студентов и желаем им дальнейших высот!</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNXZJZGZBb2t0aDA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNXZJZGZBb2t0aDA" style="height:133px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdWlqU2JWMFdJWkk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdWlqU2JWMFdJWkk" style="height:283px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMX1EtemZfMmRBNjA" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMX1EtemZfMmRBNjA" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '');
INSERT INTO `news` (`id`, `datetime`, `autor`, `title`, `preview`, `text`) VALUES
(119, 1509005443, 3, 'Уфимский международный салон «Образование будущего»', '<p style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=119" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSWZNZThkXzkxTHM" style="float:left; height:113px; width:200px" /></a>23 октября открылся&nbsp; Уфимский международный салон &laquo;Образование будущего&raquo; в выставочном комплексе ВДНХ-ЭКСПО. Мероприятие организовало Министерство образования РБ и Московский международный салон образования при поддержке главы республики</p>\r\n\r\n<p style="text-align:justify">Уфимский международный салон образования &ndash; это первая региональная экспертная площадка, организованная с использованием технологий проведения Московского международного салона образования.</p>\r\n', '<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSWZNZThkXzkxTHM" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMSWZNZThkXzkxTHM" style="float:left; height:113px; width:200px" /></a>23 октября открылся&nbsp; Уфимский международный салон &laquo;Образование будущего&raquo; в выставочном комплексе ВДНХ-ЭКСПО. Мероприятие организовало Министерство образования РБ и Московский международный салон образования при поддержке главы республики</p>\r\n\r\n<p style="text-align:justify">Уфимский международный салон образования &ndash; это первая региональная экспертная площадка, организованная с использованием технологий проведения Московского международного салона образования.&nbsp;</p>\r\n\r\n<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMa3NfdkNkeDFDUUU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMa3NfdkNkeDFDUUU" style="float:right; height:113px; width:200px" /></a>Темами Уфимского международного салона образования &quot;Образование будущего&quot;- 2017 стали: сопровождение одаренных детей в образовательных организациях, вопросы совершенствования изучения родных языков, перспективные направления развития электронного образования, взаимодействие родителей и образовательных организаций и др.</p>\r\n\r\n<p style="text-align:justify">В период трехдневной работы, Уфимский международный салон образования посетили и представили свою площадку- юнармейцы Местного отделения города Уфы Республики Башкортостан подразделение ГБПОУ УКСИВТ, совместно с Начальником Регионального Отделения ВВПОД &ldquo;Юнармия&rdquo;, Зинатуллиным Арсеном Альбертовичем.<a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMS19aZkFCX1A1NVE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMS19aZkFCX1A1NVE" style="float:left; height:150px; width:200px" /></a></p>\r\n\r\n<p style="text-align:justify">Площадку осмотрели уважаемые гости данного мероприятия, одними из которых были: министр образования Республики Башкортостан Гульназ Шафикова, заместитель Председателя оргкомитета Московского международного салона образования, помощник вице-премьера Правительства Российской Федерации Венеамин Каганов.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLWYxVE81WU4tUkk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLWYxVE81WU4tUkk" style="float:right; height:197px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMHNCb3VOQzJTbEU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMMHNCb3VOQzJTbEU" style="float:right; height:169px; width:300px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n'),
(120, 1509359996, 3, 'встреча депутата ГД со студентами ГБПОУ УКСИВТ', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT1loY0hCaUtOeUk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT1loY0hCaUtOeUk" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMcFBJTFRiVzhiOFE" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMcFBJTFRiVzhiOFE" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdmtPMVRVM3I0MFU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMdmtPMVRVM3I0MFU" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">30 октября 2017 года&nbsp; состоялась встреча депутата Государственной Думы и Государственного Собрания -Курултая Республики Башкортостан Бугеры Михаила Евгеньевича со студентами нашего колледжа.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTWE4Y3FCX29CZ28" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMTWE4Y3FCX29CZ28" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMZEZHR1JhWVZzTVU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMZEZHR1JhWVZzTVU" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMY0h6SFEwVkt1a28" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMY0h6SFEwVkt1a28" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT01CSm9KNURTTTg" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMT01CSm9KNURTTTg" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOUZORnBXV0FPTFU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOUZORnBXV0FPTFU" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRXRtTUFNdjJvU00" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMRXRtTUFNdjJvU00" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(121, 1509369145, 3, 'Курс по изучению RubyOnRails', '<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQkFtekEwaDBHWDQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMOUlsaFVicHBuc1E" style="float:left; height:101px; width:200px" /></a>Компания Техинформ совместно с кафедрой ВМиК УГАТУ объявляет 2-ой набор на курс по изучению RubyOnRails. Все новости по программе и оперативная информация будут доступны в официальнойTelegram-группе&nbsp;<a href="https://t.me/RubyCourse" target="_blank">https://t.me/RubyCourse</a>.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Стоимость: бесплатно для студентов.</p>\r\n\r\n<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQkFtekEwaDBHWDQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMQkFtekEwaDBHWDQ" style="float:right; height:283px; width:200px" /></a>Длительность: 12+ занятий по 2 академических часа. Занятия проходят еженедельно.</p>\r\n\r\n<p style="text-align:justify">Цель курса: научиться самостоятельно и эффективно осваивать новые технологии и разрабатывать профессиональные веб-приложения на фреймворке RubyOnRails.</p>\r\n\r\n<p style="text-align:justify">Стартуем: 11 ноября в 14.00 в аудитории 406 6-го корпуса УГАТУ.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Запись на курс обязательна (или через форму на сайте&nbsp;<a href="http://techinform.pro/" target="_blank">techinform.pro</a>&nbsp;или прямо здесь в чате).&nbsp;</p>\r\n\r\n<p style="text-align:justify">Подробнее на сайте:&nbsp;<a href="http://techinform.pro/course" target="_blank">techinform.pro/course</a>&nbsp;</p>\r\n', ''),
(122, 1509537006, 3, 'Расписание аудиторных занятий осенней сессии заочной формы обучения', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p><strong>ВНИМАНИЕ!</strong></p>\r\n\r\n			<p style="text-align:justify">На сайте ГБПОУ УКСИВТ опубликовано <a href="https://drive.google.com/open?id=0B-SC8AHRnwOMdWZHVkl4Vmh2SzQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий осенней сессии заочной формы обучения отделения информатики и вычислительной техники для групп: 1ИС-17, 2ИС9- 16, 2ИС-16, 5ИС- 15; 6ИС-14.</a></p>\r\n\r\n			<p style="text-align:justify">Сроки проведения сессии с 06 ноября 2017 года по 25 ноября 2017 года.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(123, 1509608224, 3, 'Поздравляем', '<p style="text-align:justify">С 29 мая по 2 июня 2017 г. в Чувашской Республики проходил Межрегиональный открытый фестиваль русского языка среди профессиональных образовательных организаций Приволжского федерального округа. Студенты колледжа вместе со своими руководителями приняли участие на фестивале в конкурсе эссе &quot;Кого из писателей (поэтов), создавших произведения на русском языке, вы бы выдвинули на получение Нобелевской премии по литературе?&quot; и были награждены СЕРТИФИКАТАМИ УЧАСТНИКОВ.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaHBkdlh3TXNFWkk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMaHBkdlh3TXNFWkk" style="height:212px; width:150px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLWxRQ3c5TmFjTDQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLWxRQ3c5TmFjTDQ" style="height:212px; width:150px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMek9zV0l3WGR5eHc" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMek9zV0l3WGR5eHc" style="height:212px; width:150px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLVpjd0JaS1NocTg" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMLVpjd0JaS1NocTg" style="height:212px; width:150px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(124, 1509608628, 3, 'Расписание звонков на 6 ноября 2017', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMUGRRTUZ6TTBOaW8" style="height:190px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify">ВНИМАНИЕ!</p>\r\n\r\n			<p style="text-align:justify">Студентам заочной формы обучения отделения информатики и вычислительной техники!</p>\r\n\r\n			<p style="text-align:justify">Обращаем ваше внимание на изменение расписания звонков на 6 ноября 2017 года (понедельник):</p>\r\n\r\n			<p style="text-align:justify">0 пара - 8.10-9.20</p>\r\n\r\n			<p style="text-align:justify">1 пара - 9.30-10.40</p>\r\n\r\n			<p style="text-align:justify">2 пара -10.50-12.00</p>\r\n\r\n			<p style="text-align:justify">3 пара -12.10-13.20</p>\r\n\r\n			<p style="text-align:justify">4 пара - 13.30-14.40</p>\r\n\r\n			<p style="text-align:justify">5 пара -14.50-16.00</p>\r\n\r\n			<p style="text-align:justify">6 пара -16.10-17.20</p>\r\n\r\n			<p style="text-align:justify">&nbsp;Отделение информатики и ВТ</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(125, 1509686310, 3, 'Первые республиканские юнармейские игры', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1K6ULQl3HXkXY3mNjK--0jPagLv7WusiZ" style="float:left; height:150px; width:150px" />В городе Уфе Республики Башкортостан с 27 по 29 октября 2017 года на базе ГБОУ &laquo;Республиканская школа-интернат № 5 среднего (полного) общего образования спортивного профиля&raquo;&nbsp;состоялись &laquo;Первые республиканские юнармейские игры&raquo; с участием юнармейских отрядов ВВПОД &laquo;ЮНАРМИЯ&raquo;&nbsp;</p>\r\n\r\n<p style="text-align:justify">Мероприятие проводится региональным отделением ВВПОД &laquo;ЮНАРМИЯ&raquo; Республики Башкортостан при поддержке Министерства молодежной политики и спорта Республики Башкортостан и ГБУ &laquo;Центр патриотического воспитания и допризывной подготовки молодежи Республики Башкортостан&raquo;.</p>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1K6ULQl3HXkXY3mNjK--0jPagLv7WusiZ" style="float:left; height:200px; width:200px" />В городе Уфе Республики Башкортостан с 27 по 29 октября 2017 года на базе ГБОУ &laquo;Республиканская школа-интернат № 5 среднего (полного) общего образования спортивного профиля&raquo;&nbsp;состоялись&laquo;Первые республиканские юнармейские игры&raquo; с участием юнармейских отрядов ВВПОД &laquo;ЮНАРМИЯ&raquo;&nbsp;</p>\r\n\r\n<p style="text-align:justify">Мероприятие проводится региональным отделением ВВПОД &laquo;ЮНАРМИЯ&raquo; Республики Башкортостан при поддержке Министерства молодежной политики и спорта Республики Башкортостан и ГБУ &laquo;Центр патриотического воспитания и допризывной подготовки молодежи Республики Башкортостан&raquo;.</p>\r\n\r\n<p style="text-align:justify">Игры&nbsp;проводятся в целях формирования чувства ответственности, гражданского долга и духовного единства, воспитания готовности к достойному и самоотверженному служению обществу и своей стране, выполнению обязанностей по защите Отечества, формированию у&nbsp;молодежи позитивного отношения к службе в Вооруженных Силах Российской Федерации, популяризации среди молодежи здорового образа жизни, деятельности патриотических, военно-спортивных клубов и общественных организаций, реализующих проекты и программы патриотической направленности.</p>\r\n\r\n<p style="text-align:justify">В программе данного мероприятия были спортивные состязания по&nbsp;направлениям:</p>\r\n\r\n<p style="text-align:justify">&bull;&nbsp;грэпплинг&nbsp;GI и&nbsp;грэпплинг&nbsp;NO GI (возрастная категория: дети от 5 до 15 лет, юноши от 16 до 19 лет, новички 20+, профи 20+);&nbsp;</p>\r\n\r\n<p style="text-align:justify">&bull;&nbsp;&laquo;лайт&nbsp;контак&raquo; (возрастная категория от 12 до 17 лет);</p>\r\n\r\n<p style="text-align:justify">&bull;&nbsp;&laquo;фулл&nbsp;контакт&raquo; (возрастная категория от 18 до 35 лет).</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">В Играх приняли участие около 500 представителей юнармейских отрядов республики, а также спортивных федераций и клубов Республики Башкортостан. Участники соревнований проявили&nbsp;невероятную&nbsp;волю к победе,&nbsp;при этом, показав свою целеустремленность, решительность, смелость, упорство и выдержку.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n'),
(126, 1510142326, 3, 'Расписание аудиторных занятий осенней сессии заочной формы обучения', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify"><strong>ВНИМАНИЕ!</strong></p>\r\n\r\n			<p style="text-align:justify">На сайте ГБПОУ УКСИВТ опубликовано <a href="https://drive.google.com/open?id=1hfOETLi_chJhyoqW_pOa3aqQBKs6NKp9" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий осенней сессии студентов заочной формы обучения отделения экономики, права и земельно-имущественных отношений для групп 2ПСО С-16, 2ЗИО С-16</a></p>\r\n\r\n			<p style="text-align:justify">Сроки проведения сессии с 20 ноября 2017 года по 01 декабря 2017 года.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(127, 1510295873, 3, 'вечер любителей поэзии «Вдохновение»', '<p style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=127"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Du3kh1B3jpN528hPbkLVp6Xn4lVSNvLb" style="float:left; height:134px; width:200px" /></a>8 ноября в актовом зале филологами был организован вечер любителей поэзии &laquo;Вдохновение&raquo; совместно с городской библиотекой &nbsp;ЦГДБ им. Ш.Худайбердина и поэтами &ndash; бардами: Н.Граховым, А. Мичуриным, В.Колесниковым.</p>\r\n\r\n<p style="text-align:justify">Ведущие вечера Тимофеев Илья (1ПО-2) и Рыбина Анджела (1П-1) настроили зал на восприятие&nbsp; тонкой поэзии в слиянии с авторской&nbsp; музыкой, знакомили с творчеством поэтов-земляков.</p>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Du3kh1B3jpN528hPbkLVp6Xn4lVSNvLb" style="float:left; height:134px; width:200px" />8 ноября в актовом зале филологами был организован вечер любителей поэзии &laquo;Вдохновение&raquo; совместно с городской библиотекой &nbsp;ЦГДБ им. Ш.Худайбердина и поэтами &ndash; бардами: Н.Граховым, А. Мичуриным, В.Колесниковым.</p>\r\n\r\n<p style="text-align:justify">Ведущие вечера Тимофеев Илья (1ПО-2) и Рыбина Анджела (1П-1) настроили зал на восприятие&nbsp; тонкой поэзии в слиянии с авторской&nbsp; музыкой, знакомили с творчеством поэтов-земляков.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1cfi15v0ejzThesRPuzil4vPG5tp18bjS" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1bHiTHbOpEk6M-YYLpfMvYN1tSfCG4irP" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1KItN577PLR1arrohYPkWQHaTQ2cq0r_I" style="float:left; height:134px; width:200px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">Прекрасный сценарий, составленный Мирасовой И.В., презентация&nbsp; Ермаковой С.Г. , заведующей сектором эстетического воспитания &nbsp;&nbsp;ЦГДБ им. Ш.Худайбердина, и самое главное- звучание авторской песни в исполнении известных поэтов- бардов оставили неизгладимое впечатление у студентов- первокурсников.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1KkTJtRxZSX_Rl3ODAvCd_FK8fCcuB4dU" style="float:left; height:298px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1EW8dv1sBrI-R7Ud4ycitOmSgoBKx210Z" style="float:left; height:298px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1JTuQ8ZerYF9QHzCJ9Qs2ioZEWw89pQib" style="float:left; height:298px; width:200px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">Вечер был великолепен еще и тем, что читали свои стихи студентка группы 1З-1 Мельникова Анита и выпускник 2008 года Шарипов Антон, ныне успешно работающий в Санкт- Петербурге программистом. Антон прочитал стихи о назначении поэта в современном обществе, а также посвятил стихотворение &laquo;85 &ndash;летию колледжа&raquo;.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1IvfpvcN-rI5UAE8HsHI_qj5V7JSVlsPC" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1nJlfT9u8pT4arVOS0NBDsxunqUSCEuKc" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1st7MFooz7jC2Jsg7UoMaz3rf_mxCubMB" style="float:left; height:134px; width:200px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">В конце состоялось награждение победителей конкурса выразительного чтения.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=15OekAWSQ05RmCCli81iG148lSowZMxol" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1bl-mqjN9m0SfCFtXq4As1-g22lRdclYk" style="float:left; height:134px; width:200px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=18w3pp4tmMm0wvB14-jGYUPPDr69_AuB_" style="float:left; height:134px; width:200px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n'),
(128, 1510301679, 3, 'День профсоюзного работника', '<div style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=128"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1zcmYoZJGZNmoxMmnF_cdN56rfCj3k7T3" style="float:left; height:243px; width:300px" /></a>8 ноября в Администрации городского округа город Уфа, в преддверии празднования Дня профсоюзного работника, состоялось награждение активистов профсоюзных организаций предприятий и учреждений города. Почетной грамотой Администрации городского округа город Уфа Республики Башкортостан&nbsp;за высокий профессионализм, многолетнюю и активную работу по защите трудовых, социально-экономических прав и интересов членов профсоюза, большой вклад в развитие системы социального партнерства в столице награжден директор Уфимского колледжа статистики, информатики и вычислительной техники Кунсбаев Сайфутдин Зайнетдинович.&nbsp;</div>\r\n', '<div style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1zcmYoZJGZNmoxMmnF_cdN56rfCj3k7T3" style="float:left; height:243px; width:300px" />8 ноября в Администрации городского округа город Уфа, в преддверии празднования Дня профсоюзного работника, состоялось награждение активистов профсоюзных организаций предприятий и учреждений города. Почетной грамотой Администрации городского округа город Уфа Республики Башкортостан&nbsp;за высокий профессионализм, многолетнюю и активную работу по защите трудовых, социально-экономических прав и интересов членов профсоюза, большой вклад в развитие системы социального партнерства в столице награжден директор Уфимского колледжа статистики, информатики и вычислительной техники Кунсбаев Сайфутдин Зайнетдинович.&nbsp;Почетные грамоты и Благодарственные письма Администрации города&nbsp;вручал заместитель главы Администрации городского округа город Уфа Рустем Газизов. Поздравляем Сайфутдина Зайнетдиновича с получением Почетной грамоты Администрации&nbsp;города,&nbsp;желаем дальнейших успехов и плодотворной работы!!!</div>\r\n\r\n<div style="text-align:justify">\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1YP9AhH1lacepSn1P0q7vjW0czjq9UKZi" style="float:left; height:424px; width:300px" /></td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1zrQueHdU9a6s6fxgOSUj7Hw2XGzR8NUa" style="float:left; height:424px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>День профсоюзного работника Республики Башкортостан будет отмечаться 12 ноября. Федерация профсоюзов республики &ndash; самая массовая общественная организация. В 23 членских организациях Федерации действуют более 5, 5 тысяч первичных профсоюзных организаций с охватом свыше 600 тысяч членов профсоюзов.&nbsp;</p>\r\n\r\n<p><img alt="" src="http://drive.google.com/uc?export=view&amp;id=188tA7b-cc4K3gIwwLZYmXSdE_57Ns5r9" style="float:left; height:291px; width:600px" /></p>\r\n</div>\r\n'),
(129, 1510313987, 3, 'Благодарность', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1jttbeR9dk0FBZ603MAxTWbeLVYZFW-eW" style="float:left; height:424px; width:300px" />Республиканский центр народного творчества выражает благодарность директору Кунсбаеву Сайфутдину Зайнетдиновичу и зам.директору по воспитательной работе Сафуановой Зульфие Аглямовне за активное участие и поддержку в проведении Республиканского фестиваля творчества детей инвалидов &quot;Радуга талантов&quot;, проходившего 5 ноября 2017 г. в&nbsp;Доме культуры Республиканского центра народного творчества.</p>\r\n\r\n<p style="text-align:justify">Фестиваль проводится в целях всесторонней активизации и реабилитации,&nbsp; создавая благоприятные&nbsp; условия&nbsp; для&nbsp; выражения&nbsp; способностей&nbsp; детей&nbsp; с ограниченными возможностями здоровья через творческую деятельность способствуя облегчению&nbsp; доступа к основным культурным ценностям и занятиям культурно &ndash; творческой деятельностью.&nbsp;Творческие конкурсы&nbsp;проведены в номинациях: выразительное чтение, вокал, танец, игра на музыкальных&nbsp;&nbsp; инструментах, гимнастические и акробатические этюды, оригинальный жанр.&nbsp;</p>\r\n', ''),
(130, 1510382175, 3, 'заседание комиссии по делам несовершеннолетних и защите их прав', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1ebNorsozR-G4oOU6DfV7a3vqhOOPTl3m" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ebNorsozR-G4oOU6DfV7a3vqhOOPTl3m" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1Dl1NkXZ0E5YTuJjpzgGsByPutnEx6mYm" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Dl1NkXZ0E5YTuJjpzgGsByPutnEx6mYm" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1-Cf313fXBs37D1qN8VEOjv-_bRN-cqZn" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1-Cf313fXBs37D1qN8VEOjv-_bRN-cqZn" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:justify">08.11.2017 года&nbsp; года на базе нашего колледжа было проведено выездное заседание комиссии по делам несовершеннолетних и защите их прав&nbsp; Администрации Кировского района городского округа г. Уфа Республики Башкортостан с участием заместителя главы Администрации Умаргалина Р.С.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1nuXspwMZ6Nv1RWZziUQ6SF148hMZW8Ch" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1nuXspwMZ6Nv1RWZziUQ6SF148hMZW8Ch" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1S7i-atTBtkGDRZLCdyMlwr1T8axrCZYW" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1S7i-atTBtkGDRZLCdyMlwr1T8axrCZYW" style="height:134px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1YoeRC7YMUzYu30kMYDDgiQ6SFBrwnewr" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1YoeRC7YMUzYu30kMYDDgiQ6SFBrwnewr" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(131, 1510568446, 3, 'Поздравляем!', '<p style="text-align:justify">Поздравляем команду девушек нашего колледжа, занявшую 3 место в Спартакиаде СПО Республики Башкортостан по шахматам.<a href="http://drive.google.com/uc?export=view&amp;id=1KvgqgKmJqxjRLWVztmO0FiaCGS9nK2xp" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1KvgqgKmJqxjRLWVztmO0FiaCGS9nK2xp" style="float:right; height:167px; width:250px" /></a></p>\r\n\r\n<p style="text-align:justify">В составе:</p>\r\n\r\n<p style="text-align:justify">Габдульманова Алсу (группа 3П-1)</p>\r\n\r\n<p style="text-align:justify">Омельченко Диана (группа 2П-4)</p>\r\n\r\n<p style="text-align:justify">Шарафутдинова Фаниля (группа 3Э-1)</p>\r\n', ''),
(132, 1510653525, 3, 'Поздравляем', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=16s_pXkexbQwJVBJMrtvENqELmW_TKz2P" style="float:left; height:225px; width:300px" />ГБПОУ УКСИВТ стал победителем в открытом&nbsp;публичном&nbsp;Всероссийском&nbsp;смотр-конкурсе образовательных организаций и был отмечен наградами. Данный конкурс проводился Управлением системы качества образования и мониторинга показателей подготовки кадров, Управлением стратегии, анализа и прогноза в сфере образования, Управлением популяризации и внедрения инновационных образовательных технологий, Управлением политики в сфере массовых мероприятий, Информационным центром методического сопровождения Агентства по современному образованию и науке Обрнаука.РФ.</p>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=16s_pXkexbQwJVBJMrtvENqELmW_TKz2P" style="float:left; height:225px; width:300px" />ГБПОУ УКСИВТ стал победителем в открытом&nbsp;публичном&nbsp;Всероссийском&nbsp;смотр-конкурсе образовательных организаций и был отмечен наградами. Данный конкурс проводился Управлением системы качества образования и мониторинга показателей подготовки кадров, Управлением стратегии, анализа и прогноза в сфере образования, Управлением популяризации и внедрения инновационных образовательных технологий, Управлением политики в сфере массовых мероприятий, Информационным центром методического сопровождения Агентства по современному образованию и науке Обрнаука.РФ.<br />\r\nОткрытый публичный Всероссийский смотр-конкурс образовательных организаций проводился с 23 января по 28 февраля 2017 г. на всероссийском уровне. Открытый публичный Всероссийский смотр-конкурс образовательных организаций проходил в инновационном онлайн &mdash; формате.<br />\r\nОбразовательные организации через специализированные интернет &mdash; интерфейсы формировали выставочный контент и принимали участие в открытых мероприятиях дистанционно. В открытом публичном Всероссийском смотре-конкурсе образовательных организаций участвовали образовательные организации РФ, имеющие государственную аккредитацию.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1pFyHu4gc-zwl35--VOsYFyRfuVLeCWUf" style="float:left; height:424px; width:300px" /></td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1B0wtNFUDgnBgLkzfetiJ2FuNQFrS6icp" style="float:left; height:424px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n'),
(133, 1510731700, 3, 'Приглашение', '<p style="text-align:center">ВНИМАНИЕ!</p>\r\n\r\n<p style="text-align:center">Все желающие приглашаются на ФУТБОЛ</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ZuZZ3_rZL_yMZEJ5rC-LPV4T9TwsF2HA" style="height:165px; width:100px" /></td>\r\n			<td style="text-align:center"><strong><span style="font-size:14px">ФК &quot;УФА&quot;</span></strong></td>\r\n			<td style="text-align:center">-</td>\r\n			<td style="text-align:center"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1dA0fervmfnOW-pF87XE8Fd3MhkCy_mV7" style="height:112px; width:100px" /></td>\r\n			<td style="text-align:center"><strong><span style="font-size:14px">ФК &quot;УРАЛ&quot;</span></strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:center"><strong><span style="font-size:20px">19 ноября 2017 года</span></strong><span style="font-size:14px">&nbsp;</span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><strong>Сбор в 12 часов 30 минут возле&nbsp;центрального входа на стадион &quot;Нефтяник&quot;</strong></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px">Обещают обширную программу (шоу, горячий чай и каша) <strong>БЕСПЛАТНО!!!</strong></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px">Вход <strong>БЕСПЛАТНЫЙ</strong></span></p>\r\n', ''),
(134, 1510828333, 3, 'Брифинг', '<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://uksivt.ru/web/index.php?r=site/page&amp;id=31"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ahjd4KDLQQqW8-7SSq0Fw2Anp2_VDnto" style="height:225px; width:300px" /></a></td>\r\n			<td>\r\n			<p>Читаем&nbsp;свежий&nbsp;<a href="http://uksivt.ru/web/index.php?r=site/page&amp;id=127">ноябрьский</a>&nbsp;выпуск&nbsp;газеты нашего любимого колледжа &ldquo;<a href="http://uksivt.ru/web/index.php?r=site/page&amp;id=31">Брифинг</a>&rdquo;!</p>\r\n\r\n			<p>Открыть и прочитать выпуск&nbsp;<a href="http://uksivt.ru/web/index.php?r=site/page&amp;id=127">здесь</a></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '');
INSERT INTO `news` (`id`, `datetime`, `autor`, `title`, `preview`, `text`) VALUES
(135, 1510830469, 3, 'Первые Парламентские дебаты', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1zlQOXXtrDSKVhCYXWud4-_gZ4dre7RUB" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1zlQOXXtrDSKVhCYXWud4-_gZ4dre7RUB" style="height:200px; width:300px" /></a></td>\r\n			<td>\r\n			<p style="text-align:justify">16.11.2017 года в рамках декады ПЦК юридических дисциплин проходили Первые Парламентские дебаты среди студентов всех курсов ПСО, ПД, ПСА. Целью проведения данного мероприятия служили: формирование навыков ораторского искусства, преодоление неуверенности и нерешительности у студентов в публичном выступлении, а также развитие юридического мышления.</p>\r\n\r\n			<p style="text-align:justify">Благодарим всех кто принял участие и поздравляем победителей!</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Iu7fiPk2gxjAyC0Nu9L8908xdit06iqA" style="float:left; height:133px; width:200px" />В дебатах приняли участие 6 групп в составе 24 человек. Решением коллегии судей парламентских дебатов, в составе Чугунова П. М., Хамидовой Д. С. и Галиевой Р. Ф., были определены победители дебатов.</p>\r\n\r\n<p style="text-align:justify">Ими стали:</p>\r\n\r\n<p style="text-align:justify">В номинации лучший оратор первое место было присвоено Исламову Тимуру с присуждением титула &laquo;лучший спикер&raquo;, второе место получил Закиров Айнур &nbsp;и третье Трофимов Никита.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ObGG0UtnkxFWC53JbY_s2Bck_M9r-lp-" style="height:200px; width:300px" /></p>\r\n\r\n<p style="text-align:justify">В номинации команда победительница первое место было присвоено группе 2уПО-2 БашГУ, в составе Цхварадзе Анастасии, Мельниковой Ксении, Ермолаевой Ксении и Хасановой Айсылу.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1MG--iO0U_1NljddmCcKcMBn4cHFuvA9G" style="height:200px; width:300px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n'),
(136, 1510832875, 3, 'Заседание круглого стола', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1NxgUTkOdMsu6Y48JGpQY_THJ_kcBhKdP" style="float:left; height:200px; width:300px" />15 ноября 2017 года в рамках декады ПЦК юридических дисциплин колледжа УКСИВТ было проведено заседание круглого стола на тему: &quot;Аборт как социальный феномен с точки зрения Российской молодежи&quot; среди студентов второго курса юридических специальностей с участием Кандаровой Динары Фаиловны-врача акушер-гинеколога клиники &quot;Здоровье женщины&quot;, а также Искендеровой Райфы Сабитовны- следователя отдела полиции Кировского ОВД. В мероприятии также участвовали председатель ПЦК юридических дисциплин- Чугунов П.М., преподаватель колледжа БГУ- Галиев Р.Ф.&nbsp;</p>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1NxgUTkOdMsu6Y48JGpQY_THJ_kcBhKdP" style="float:left; height:200px; width:300px" />15 ноября 2017 года в рамках декады ПЦК юридических дисциплин колледжа УКСИВТ было проведено заседание круглого стола на тему: &quot;Аборт как социальный феномен с точки зрения Российской молодежи&quot; среди студентов второго курса юридических специальностей с участием Кандаровой Динары Фаиловны-врача акушер-гинеколога клиники &quot;Здоровье женщины&quot;, а также Искендеровой Райфы Сабитовны- следователя отдела полиции Кировского ОВД. В мероприятии также участвовали председатель ПЦК юридических дисциплин- Чугунов П.М., преподаватель колледжа БГУ- Галиев Р.Ф.&nbsp;</p>\r\n\r\n<p style="text-align:justify">В ходе данного мероприятия были рассмотрены наиболее важные и актуальные вопросы медицинских последствий абортов, морально этических проблем ИПБ, а также юридической отвественности и некоторых проблем уголовной ответственности за незаконное производство аборта.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=176TCYyM1XloXLMA5KOZ_C8ppaZzmUwo0" style="float:left; height:200px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1JoqXWRr4K0xr3wIqSvK-h_2OWCo1i6Vb" style="float:left; height:200px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">По результатам проведённого мероприятия участникам и гостям были вручены грамоты и благодарственные письма.&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1xjrP_VfV4PhG57zl5oBJx9d4f-oDRqPq" style="float:left; height:200px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ij4zQbNZx-HKXNMFUOE58SW6PAAtJIP5" style="float:left; height:200px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n'),
(137, 1511263445, 3, 'Расписание аудиторных занятий осенней сессии заочной формы обучения', '<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=0B-SC8AHRnwOMNnVPQ2s3d1U4X0E" style="height:193px; width:200px" /></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td>\r\n			<p style="text-align:justify"><strong>ВНИМАНИЕ!</strong></p>\r\n\r\n			<p style="text-align:justify">На сайте ГБПОУ УКСИВТ опубликовано <a href="https://drive.google.com/open?id=1Qm_KU6cdtOfm-bORTw9RpUqn23a7WXMk" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Расписание аудиторных занятий осенней сессии студентов заочной формы обучения отделения экономики, права и земельно-имущественных отношений для групп 1ПСО С-17, 1ЗИО С-17</a></p>\r\n\r\n			<p style="text-align:justify">Сроки проведения сессии с 27&nbsp;ноября 2017 года по 08&nbsp;декабря 2017 года.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(138, 1511430214, 3, 'Научно-практическая конференция «Особенности квалификации преступлений»', '<p style="text-align:justify">22 ноября 2017 года в рамках декады ПЦК юридических дисциплин специальностей ПСА и ПД была проведена Научно-практическая конференция &laquo;Особенности квалификации преступлений&raquo; с участием представителей МВД РБ. В мероприятии также участвовали ведущие преподаватели&nbsp; ПЦК ПСА и ПД, обучающие студентов специальным дисциплинам .Необходимо отметить студентов: Григорьеву Марину Машкову Дарью группа 3по-2 выступившую с докладом &laquo;Особенности квалификации преступлений против собственности&raquo;, и Григорьеву Марину группа 3по-3, которая рассказала об особенностях квалификации преступлений коррупционной направленности.&nbsp;</p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1_8LKFGC8Wjwa9pwZKUbQewXRJsTBxsU4" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1_8LKFGC8Wjwa9pwZKUbQewXRJsTBxsU4" style="height:84px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1REGvZ3HSUOgA2EByzO4OB9e_0vYGEo50" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1REGvZ3HSUOgA2EByzO4OB9e_0vYGEo50" style="height:84px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1HXWIeCnBjbXHAHCdmtwa5vPHAyQThMsD" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1HXWIeCnBjbXHAHCdmtwa5vPHAyQThMsD" style="height:84px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1W6I23RkgIfEjqt40bVSOYLX-HbrXAa2o" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1W6I23RkgIfEjqt40bVSOYLX-HbrXAa2o" style="height:84px; width:150px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(139, 1511760617, 3, 'Студенты ГБПОУ УКСИВТ устроили флешмоб в честь 85-летия родного колледжа.', '<p style="text-align:center"><iframe frameborder="0" height="315" src="https://www.youtube.com/embed/EiURdxz-bVc" width="560"></iframe></p>\r\n', ''),
(140, 1511854023, 3, '100 лучших ссузов России', '<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=1LNzHXeLjjKFCv1xYH6-s2RplnAnGng8p" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1LNzHXeLjjKFCv1xYH6-s2RplnAnGng8p" style="float:left; height:224px; width:150px" /></a>С 27 по 30 октября 2017 года&nbsp; прошла XII ежегодная Всероссийская конференция&nbsp; &quot;Проблемы и перспективы развития среднего профессионального образования в России&quot;.<br />\r\nТрадиционно в Санкт-Петербурге собрались руководители ведущих ссузов России. Мероприятие зарекомендовало себя как ведущее событие в стране этого профиля. Форум проводился при участии Министерства образования и науки РФ, профильных комитетов Государственной Думы РФ, Совета Федерации РФ, Союза директоров ссузов России, Международной академии качества и маркетинга и журнала &quot;Директор ссуза&quot;<a href="http://drive.google.com/uc?export=view&amp;id=1-xBt1OTV269H185pfEFaciO2jK7iceN3" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1-xBt1OTV269H185pfEFaciO2jK7iceN3" style="float:right; height:134px; width:200px" /></a><br />\r\n&nbsp; &nbsp; &nbsp;В рамках мероприятия прошло награждение лауреатов конкурса &quot;100 лучших ссузов России&quot;, где наш колледж вновь стал победителем!!!! Мы были награждены золотой медалью, как лучшая образовательная организация страны, добившаяся наиболее высоких результатов в области подготовки специалистов со средним специальным образованием.&nbsp;</p>\r\n', ''),
(141, 1511856139, 3, 'Поздравляем!', '<p style="text-align:justify">От всей души поздравляем директора нашего колледжа Кунсбаева Сайфутдина Зайнетдиновича с вручением почетного знака &quot;Директор года -2017&quot; и присвоением звания &quot;Почетный член Международной академии качества и маркетинга&quot;&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1eyuTiwpRnrrUwelzq3oPtS900UzHCywY" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1eyuTiwpRnrrUwelzq3oPtS900UzHCywY" style="height:201px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=17fBxCIkt5-RHvUMNS7N3hcJbDozTanIj" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=17fBxCIkt5-RHvUMNS7N3hcJbDozTanIj" style="height:201px; width:300px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(142, 1511862916, 3, 'участие в конференции', '<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=1hRJ8HB90GD8c2kK0eNCe0mcvOV6TXvwr" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1hRJ8HB90GD8c2kK0eNCe0mcvOV6TXvwr" style="float:left; height:225px; width:300px" /></a>24 ноября студенты колледжа под руководством преподавателей Гареевой С.Т., Шакуровой Д.М., Сыртлановой Л.А. приняли участие в республиканской научно-практической конференции &laquo;Учебно-исследовательская деятельность студентов как фактор формирования профессиональных компетенций в среднем профессиональном образовании&raquo;. <a href="http://drive.google.com/uc?export=view&amp;id=1GniH5c-o3OvjeTqC10hgiLw_BI4KYBO5" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1GniH5c-o3OvjeTqC10hgiLw_BI4KYBO5" style="float:right; height:212px; width:150px" /></a>Выступление наших студентов Набиевой Динары и Шаймарданова Олега &nbsp;&nbsp;с проектом &laquo;Жизнь после смерти (проблема бытовых отходов)&raquo; (науч. рук. Шакурова Д.М.) в работе секции &laquo;В мире профессий. Формирование молодых профессионалов&raquo; было отмечено жюри дипломом III степени. В сборнике материалов конференции опубликованы работы студентов I курса специальности 09.02.01 Компьютерные системы и комплексы Залиева М.Э., Мамбетова Р.Р. (науч.рук. Гареева С.Т.), а также вышла статья преподавателя Шакуровой Д.М. &laquo;Перспективы развития экотуризма в Республике Башкортостан и внедрение его в образовательные организации&raquo;. Поздравляем всех участников и желаем новых побед в научных поисках!</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1ewsrZ1R_ABC5Zm4TpWjFVGLFOLSgfJEQ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ewsrZ1R_ABC5Zm4TpWjFVGLFOLSgfJEQ" style="float:left; height:139px; width:200px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=112abndUtCIVUMrhSOsWiiYHbgeugB_5x" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=112abndUtCIVUMrhSOsWiiYHbgeugB_5x" style="float:left; height:138px; width:200px" /></a></td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=16P66GdhIORCIhozi7kKiMF8to6NL0fjr" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=16P66GdhIORCIhozi7kKiMF8to6NL0fjr" style="float:left; height:138px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(143, 1511863600, 3, 'Поздравляем!', '<p style="text-align:justify">Студентка&nbsp;группы 1ПО-1 Китабова Алсия Равильевна стала победителем I степени на всероссийской олимпиаде для студентов по дисциплине &quot;Экологические основы природопользования&quot;, а также получила диплом победителя II степени по дисциплине &quot;Экология&quot;. Научный руководитель Шакурова Динара Мусанифовна.</p>\r\n\r\n<p style="text-align:justify">Молодец, Алсия, так держать!</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1xFpx98e2F8JVIr4J2cPbaILiwpB7BAxx" style="height:424px; width:300px" /></td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1O3S5wPB6lgzyrhAsPcywv1wNjKB6LuRp" style="height:424px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', ''),
(144, 1511931515, 3, 'Поздравляем!', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=13DfwjtJgz76p8Z7S-h6I7Zwl6UqbQxnn" style="float:left; height:255px; width:250px" />16-17 ноября 2017 года в городе Октябрьский проходил отборочный этап соревнований на право участия в III Региональном &nbsp;чемпионате &quot;Молодые профессионалы&quot; (WorldSrills Russiа) Республики Башкортостан в 2017 году по компетенции &quot;Геодезия&quot;.&nbsp;</p>\r\n\r\n<p style="text-align:justify">От УКСИВТ участие приняла команда из студентов 3 курса группы 3 З-1 по специальности 21.02.05 &quot;Земельно-имущественные отношения&quot; &nbsp;в составе: Гаврилова Ирина и Пестрикова Наталья.</p>\r\n\r\n<p style="text-align:justify">Наша команда удостоилась диплома II степени.</p>\r\n', ''),
(145, 1512114966, 3, 'День борьбы со СПИДом', '<p><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1fgFIWOOsFcMk-gS6Yz9_TbNZYcIvU7wc" style="height:450px; width:600px" /></p>\r\n', ''),
(146, 1512129710, 3, 'Аттестация', '<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1PFR67Xo3Nkm2TTmbMtqDL2XRptKQx9hw" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1PFR67Xo3Nkm2TTmbMtqDL2XRptKQx9hw" style="height:101px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1e4DWZSHH0Fhq89B_Aw-HvHmsjXaxMFii" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1e4DWZSHH0Fhq89B_Aw-HvHmsjXaxMFii" style="height:101px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1XYX6AiAlIjV_YtyYEuUrs4IYW2eA4bJs" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1XYX6AiAlIjV_YtyYEuUrs4IYW2eA4bJs" style="height:101px; width:150px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1ExrKrTGiQ28E0lW4aDHeHw9vKd-PtIa6" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1ExrKrTGiQ28E0lW4aDHeHw9vKd-PtIa6" style="height:101px; width:150px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">В соответствии с Порядком проведения аттестации педагогических работников организаций, осуществляющих образовательную деятельность, утверждённым приказом Министерства образования и науки Российской Федерации от 7 апреля 2014 года №276, в колледже 29.11.2017 состоялось заседание аттестационной комиссии по аттестации педагогических работников в целях подтверждения соответствия педагогических работников занимаемым ими должностям &laquo;преподаватель&raquo; на основе оценки их профессиональной деятельности. На заседании присутствовали все члены аттестационной комиссии. В отношении 15 преподавателей, подлежащих аттестации, принято единогласное решение о соответствии ими занимаемой должности &laquo;преподаватель&raquo;. Очередное заседание по аттестации педагогических работников на соответствие занимаемой ими должности &laquo;преподаватель&raquo; состоится в декабре месяце согласно графику аттестации.</p>\r\n', ''),
(147, 1512378451, 3, 'День информатики', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1GlRCPhepoSk50O59mfEclnGLNDnGFYxh" style="height:256px; width:600px" />Сегодня технологический прогресс весьма удачно распространяется на все области нашей жизни. Трудно себе представить, как пришлось бы обходиться работникам торговли, образования, промышленности или простому человеку в быту без компьютеров, электронного автоматического оборудования со специальным программным обеспечением.</p>\r\n', ''),
(148, 1512382847, 3, 'КВН', '<p style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=148"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1C78j8dvDAlExxpxxYlsmgWC8xMys2Ak2" style="float:left; height:225px; width:300px" /></a>Как известно, без шуток и смеха в нашей жизни было бы жить&nbsp; просто невыносимо и бессмысленно!&nbsp; 30 ноября 2017 года, ознаменовался приятным событием для студентов проживающих в общежитии, ребят ждал настоящий КВН, к которому они усердно и ответственно готовились. Тема КВН &laquo;Экология ! &raquo;.</p>\r\n\r\n<p style="text-align:justify">Игра была проведена весело, живо, интересно и, конечно, профессионально. На этот раз соперничали три &nbsp;команды &laquo;Антихайп&raquo; ,&laquo;Чистая планета &raquo; и &laquo;Адреналин&raquo;, в состав которых вошли учащиеся 1,2,3 курсов.</p>\r\n', '<p style="text-align:justify"><a href="http://drive.google.com/uc?export=view&amp;id=1C78j8dvDAlExxpxxYlsmgWC8xMys2Ak2" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1C78j8dvDAlExxpxxYlsmgWC8xMys2Ak2" style="float:left; height:225px; width:300px" /></a>Как известно, без шуток и смеха в нашей жизни было бы жить&nbsp; просто невыносимо и бессмысленно!&nbsp; 30 ноября 2017 года, ознаменовался приятным событием для студентов проживающих в общежитии, ребят ждал настоящий КВН, к которому они усердно и ответственно готовились. Тема КВН &laquo;Экология ! &raquo;.</p>\r\n\r\n<p style="text-align:justify">Игра была проведена весело, живо, интересно и, конечно, профессионально. На этот раз соперничали три &nbsp;команды &laquo;Антихайп&raquo; ,&laquo;Чистая планета &raquo; и &laquo;Адреналин&raquo;, в состав которых вошли учащиеся 1,2,3 курсов. Хочется отметить, остроту шуток, интересные сценки, смешные миниатюры и актерское мастерство, которое показали команды. Соревновались команды в трех конкурсах: Визитная карточка, конкурс капитанов, и домашнее задание.</p>\r\n\r\n<p style="text-align:justify">Меткость слова, острота взгляда, высота голоса, глубина погружения в образ, продолжительность оваций, активность болельщиков &ndash; это только некоторые из критериев, оцениваемых жюри.</p>\r\n\r\n<p style="text-align:justify">По итогам игры победителями&nbsp; стали , команды &laquo;Антихайп&raquo; и &laquo;Чистая планета&raquo;, которые получили &nbsp;сладкие подарки. Команда &laquo;Адреналин&raquo; тоже не осталась без внимания.</p>\r\n\r\n<p style="text-align:justify">КВН закончился, а воспоминания остались надолго.</p>\r\n'),
(149, 1512537886, 3, 'Профсоюзные новости', '<p style="text-align:justify">22 ноября 2017 года Республиканский союз &ldquo;Федерация профсоюзов Республики Башкортостан&rdquo; ЧОУ ДПО &ldquo;Институт повышение квалификации профсоюзных кадров&rdquo;&nbsp; провели курсы по программе &ldquo;Информационное обеспечение профсоюзной деятельности&rdquo;, посвященное Году профсоюзной информации.&nbsp;Наши студенты Шарафутдинова Луиза (группа 2ПСА-1),&nbsp;Забихуллина Лейсан&nbsp; (группа 1ПД-2),&nbsp;Файзуллина Эльза (группа 2УКС-1),&nbsp;Апкаликов Константин (группа 2И-1) приняли активное участие.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=110k6nKvISxPkf6lh5c-i_ibsDarZsfmv" style="height:201px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=188vimtQVWZhXQjEAWBsMuVYITF5pFg4o" style="height:201px; width:268px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<p style="text-align:justify">22 ноября 2017 года Республиканский союз &ldquo;Федерация профсоюзов Республики Башкортостан&rdquo; ЧОУ ДПО &ldquo;Институт повышение квалификации профсоюзных кадров&rdquo;&nbsp; провели курсы по программе &ldquo;Информационное обеспечение профсоюзной деятельности&rdquo;, посвященное Году профсоюзной информации.&nbsp;Наши студенты Шарафутдинова Луиза (группа 2ПСА-1),&nbsp;Забихуллина Лейсан&nbsp; (группа 1ПД-2),&nbsp;Файзуллина Эльза (группа 2УКС-1),&nbsp;Апкаликов Константин (группа 2И-1) приняли активное участие.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=110k6nKvISxPkf6lh5c-i_ibsDarZsfmv" style="height:201px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=188vimtQVWZhXQjEAWBsMuVYITF5pFg4o" style="height:201px; width:268px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">На курсах обсуждались важные темы :</p>\r\n\r\n<p style="text-align:justify">-Права на получение стипендий (академических и социальных)</p>\r\n\r\n<p style="text-align:justify">-Трудовые права (по трудоустройству)</p>\r\n\r\n<p style="text-align:justify">-Повышение квалификации профсоюзных кадров и др.</p>\r\n\r\n<p style="text-align:justify">Представители Министерства образования и представители Федерации профсоюзов провели различные тренинги.</p>\r\n\r\n<p style="text-align:justify">По окончанию, каждый участник получил сертификат в объеме 8 часов.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1WhslXxWDqCEBm2FmvZJYPqWlDtypiZYX" style="height:178px; width:239px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=13f8wOObgqB4eq3fpKYe7SyWNDJGn3oVj" style="height:178px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">Также от &nbsp;Профсоюза колледжа в качестве волонтера, активно проявили&nbsp; студентки группы 2ПСА-1 Ситдикова Лейсян и Шарафутдинова Луиза.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1lwziL1gYAxTOvPUSh08LEy7KUn9tNMhK" style="height:400px; width:300px" /></td>\r\n			<td>&nbsp;</td>\r\n			<td><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1l8AsvVBoh_tMu0TjFbAn-pHxJQE8uI5D" style="height:400px; width:300px" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n'),
(150, 1512726096, 3, 'Конференция', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1I2lLeX-RL8qDr4bH7T0RAC3TIp7dvQ8c" style="float:left; height:225px; width:300px" />6 декабря 2017 в колледже состоялась городская научно-практическая конференция на тему: &laquo;Эколого-экономические аспекты формирования рационального природопользования при регулировании земельно-имущественных отношений в Республике Башкортостан&raquo;. В конференции приняли участие преподаватели и студенты колледжей ГБПОУ УКСИВТ и ГАОУ УТЭК, колледж ФГБОУ ВО &laquo;Башкирский государственный университет&raquo;. Для участия в работе конференции были приглашены Р.И. Ишкинин, начальник отдела определения кадастровой стоимости ФГБОУ ФКП &laquo;Росреест&raquo;, Т.А. Жбанова, заместитель руководителя по связям с общественностью ООО &laquo;Респект&raquo;.</p>\r\n', '<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1I2lLeX-RL8qDr4bH7T0RAC3TIp7dvQ8c" style="float:left; height:225px; width:300px" />6 декабря 2017 в колледже состоялась городская научно-практическая конференция на тему: &laquo;Эколого-экономические аспекты формирования рационального природопользования при регулировании земельно-имущественных отношений в Республике Башкортостан&raquo;. В конференции приняли участие преподаватели и студенты колледжей ГБПОУ УКСИВТ и ГАОУ УТЭК, колледж ФГБОУ ВО &laquo;Башкирский государственный университет&raquo;. Для участия в работе конференции были приглашены Р.И. Ишкинин, начальник отдела определения кадастровой стоимости ФГБОУ ФКП &laquo;Росреест&raquo;, Т.А. Жбанова, заместитель руководителя по связям с общественностью ООО &laquo;Респект&raquo;.</p>\r\n\r\n<p style="text-align:justify">В ходе проведения конференции были проанализированы эколого-экономические аспекты рационального природопользования, экономические методы управления территориями и оценки недвижимости с целью обеспечения экологической безопасности в Республике Башкортостан.</p>\r\n\r\n<p style="text-align:justify">Лучшими работами по секциям признаны:</p>\r\n\r\n<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1wRPJL8lTzygY4fOZaV49DIIXuWuDnDMw" style="float:right; height:400px; width:300px" />Секция I. &laquo;Состояние природных ресурсов и окружающей среды Республики Башкортостан&raquo;:</p>\r\n\r\n<p style="text-align:justify">1 место. Студенты ГБПОУ УКСИВТ Чебыкин С., Вахмянин К., Кильмухаметова А. &laquo;Организация утилизации отходов производства и потребления на примере МУП &laquo;Спецавтохозяйство по очистке города&raquo; ГО г.Уфа Республики Башкортостан&raquo;. Научный руководитель: Акчева Д.М.</p>\r\n\r\n<p style="text-align:justify">2 место. Студент ГБПОУ УКСИВТ Карпова А. &laquo;Состояние и охрана воздушного бассейна, лесов, водных и земельных ресурсов на территории Республики Башкортостан&raquo;. Научный руководитель: Юнусова Л.И.</p>\r\n\r\n<p style="text-align:justify">3 место. Студент ГБПОУ УКСИВТ Загидуллина И. &laquo;Основные направления рационализации природопользования и охраны окружающей среды в Республике Башкортостан&raquo;. Научный руководитель: Акчева Д.М.</p>\r\n\r\n<p style="text-align:justify">3 место. Студент ГАОУ УТЭК Хабибуллина Р. &laquo;Территории специального назначения ГО г.Уфа Республики Башкортостан&raquo;.&nbsp; Научный руководитель: Минниханова Г.Ф.</p>\r\n\r\n<p style="text-align:justify"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1QaroL4sLmGk7QeBJhHdv0WViD7RvOI-X" style="float:left; height:400px; width:300px" />Секция II. &laquo;Анализ системы рационального управления земельными ресурсами с использованием данных экологического мониторинга окружающей среды в муниципальных районах Республики Башкортостан&raquo;:</p>\r\n\r\n<p style="text-align:justify">1 место. Студенты ГБПОУ УКСИВТ Ходарцевич М. &laquo;Сравнительная эколого-экономическая оценка на примере городов Республики Башкортостан&raquo;.&nbsp; Научный руководитель: Байсакова Р.Р.</p>\r\n\r\n<p style="text-align:justify">2 место. Студенты ГБПОУ УКСИВТ Реброва Ю., Шамсутдинов И. &laquo;Сравнительная эколого-экономическая оценка на примере районов Республики Башкортостан&raquo;.&nbsp; Научный руководитель: Байсакова Р.Р.</p>\r\n\r\n<p style="text-align:justify">2 место. Студент ГАОУ УТЭК Яковлева Д. &laquo;Благоустройство городских территорий на примере этнопарка &laquo;Ватан&raquo; ГО г.Уфы Республики Башкортостан&raquo;.&nbsp; Научный руководитель: Минниханова Г.Ф.</p>\r\n\r\n<p style="text-align:justify">3 место. Студенты ГБПОУ УКСИВТ Минязева К. &laquo;Современная экологическая обстановка на территории Республики Башкортостан&raquo;. Научный руководитель: Ибраев Б.М.</p>\r\n\r\n<p style="text-align:justify">3 место. Студенты ГБПОУ УКСИВТ Зиганшина Э., Васильева В. &laquo;Эколого-экономическая оценка на примере городов Республики Башкортостан&raquo;. Научный руководитель: Юнусова Л.И.</p>\r\n\r\n<p style="text-align:justify">Секция III. &laquo;Основные экологические проблемы и инновационные пути их решения в целях устойчивого развития Республики Башкортостан&raquo;:</p>\r\n\r\n<p style="text-align:justify">1 место. Студент ГАОУ УТЭК Хайруллина К. &laquo;Совершенствование эколого-экономических подходов и проблемы строительства мечети &laquo;Аль-рахим&raquo; в ГО г. Уфа Республики Башкортостан&raquo;.&nbsp; Научный руководитель: Халикова Л.Р.</p>\r\n\r\n<p style="text-align:justify">2 место. Студент ГБПОУ УКСИВТ Тенякова Т. &laquo;Развитие и внедрение природосберегающих и безотходных технологий в районах Республики Башкортостан в промышленных, сельскохозяйственных, лесопромышленных производствах&raquo;.&nbsp; Научный руководитель: Байсакова Р.Р.</p>\r\n\r\n<p style="text-align:justify">2 место. Студенты ГБПОУ УКСИВТ Сагадеев О., Миков В. &laquo;Развитие системы мониторинга с помощью новых геоинформационных технологий&raquo;.&nbsp; Научный руководитель: Байсакова Р.Р.</p>\r\n\r\n<p style="text-align:justify">3 место. Студент ГАОУ УТЭК Губанова А. &laquo;Совершенствование управления ТБО ГО г.Уфы Республики Башкортостан&raquo;. Научный руководитель: Минниханова Г.Ф.</p>\r\n\r\n<p style="text-align:justify">3 место. Студент колледжа БашГУ Петрова К. &laquo;Поиск направлений оптимизации при эколого-экономической оценке ЗИК на основе создания единой информационно-аналитической базы&raquo;. Научный руководитель: Нагаева З.Т.</p>\r\n\r\n<p style="text-align:justify">Благодарим студентов и преподавателей за активное участие в научно-практической конференции и желаем дальнейших успехов в научно-исследовательской деятельности!</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n'),
(151, 1513160533, 3, 'Работа со студентами', '<p style="text-align:justify">13 декабря 2017 года&nbsp; на базе колледжа старшим инспектором&nbsp; Центра общественной безопасности города Уфы Адгамовым И.Я. и&nbsp; инспекторами Тимергалиевой Л.К., Габдуллиной Ю.Ф., Яковлевой Л.Н., Кадыргуловой Г.А. были&nbsp; проведены&nbsp; лекции среди студентов первого курса на тему: &laquo;Уголовные и административные правонарушения&raquo;, &laquo;Кража и мелкое хищение&raquo;.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="https://goo.gl/8cfoSt" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="https://goo.gl/8cfoSt" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="https://goo.gl/d8BqoU" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="https://goo.gl/d8BqoU" style="height:150px; width:200px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="https://goo.gl/Su3JwD" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="https://goo.gl/Su3JwD" style="height:150px; width:200px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '');
INSERT INTO `news` (`id`, `datetime`, `autor`, `title`, `preview`, `text`) VALUES
(152, 1513243812, 3, '8 декабря в городе Уфе состоялся республиканский слет «Юнармии» Башкортостана.', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><a href="http://uksivt.ru/?r=news/select&amp;id=152"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1D1yXIalizXFWqgKmm2DhK8Mqwwfi10bf" style="float:left; height:167px; width:250px" /></a>Организаторами мероприятия выступили Министерство молодежной политики и спорта Республики Башкортостан, Военный комиссариат Республики Башкортостан, Центр патриотического воспитания и допризывной подготовки молодежи Республики Башкортостан, Региональное отделение ВВПОД &laquo;Юнармии&raquo;.</p>\r\n\r\n<p style="text-align:justify">В cлете приняли участие студенты подразделения ГБПОУ УКСИВТ, а также воспитанники военно-патриотических клубов.</p>\r\n', '<p style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=1D1yXIalizXFWqgKmm2DhK8Mqwwfi10bf" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1D1yXIalizXFWqgKmm2DhK8Mqwwfi10bf" style="height:200px; width:300px" /></a></p>\r\n\r\n<p style="text-align:justify">Организаторами мероприятия выступили Министерство молодежной политики и спорта Республики Башкортостан, Военный комиссариат Республики Башкортостан, Центр патриотического воспитания и допризывной подготовки молодежи Республики Башкортостан, Региональное отделение ВВПОД &laquo;Юнармии&raquo;.</p>\r\n\r\n<p style="text-align:justify">В cлете приняли участие студенты подразделения ГБПОУ УКСИВТ, а также воспитанники военно-патриотических клубов.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1Af445ZV2SheOnovWojh-jiS58VfXwjp4" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Af445ZV2SheOnovWojh-jiS58VfXwjp4" style="height:200px; width:300px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1V9qzEs8zYNBnoNt8g0CmB-cOdft0wjEJ" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1V9qzEs8zYNBnoNt8g0CmB-cOdft0wjEJ" style="height:199px; width:300px" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">В рамках мероприятия состоялась &nbsp;церемония принятия лучших ребят республики в ряды Всероссийского детско-юношеского военно-патриотического общественного движения &laquo;Юнармия&raquo;, сообщили организаторы. Гости вручили почетные значки юнармейцам.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://drive.google.com/uc?export=view&amp;id=1b75_DVJeGBWgEUgyeMRFhOB1pm2FEHxz" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1b75_DVJeGBWgEUgyeMRFhOB1pm2FEHxz" style="height:420px; width:280px" /></a></td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p><a href="http://drive.google.com/uc?export=view&amp;id=1eXYCWehvTRBTf3VEFMdL1EZkWH1T2tI7" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=18rwfinJGBOAW1hXrNVDFuzXR3DMdf30O" style="height:201px; width:300px" /></a></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><a href="http://drive.google.com/uc?export=view&amp;id=1eXYCWehvTRBTf3VEFMdL1EZkWH1T2tI7" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1eXYCWehvTRBTf3VEFMdL1EZkWH1T2tI7" style="height:202px; width:300px" /></a></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">Подвели итоги уходящего года, где министр молодежной политики и спорта РБ Андрей Иванюта, доложил о реализации общественных проектов Приволжского федерального округа в 2017 году, направленных на патриотическое воспитание подростков и молодежи. Также обсудили Концепцию системы общественных проектов Приволжского федерального округа на 2018 год, деятельность регионального штаба Всероссийского военно-патриотического движения &laquo;Юнармия&raquo; в этом и следующего году, а также работа его местного отделения в Межгорье, итоги проведения Окружного слета поисковых отрядов ПФО &laquo;Никто не забыт&raquo; в регионе.</p>\r\n\r\n<p style="text-align:justify">&laquo;На сегодняшний день юнармейские отделения созданы во всех муниципальных районах и городских округах республики. В ряды движения приняты более пяти тысяч детей в возрасте от восьми до 18 лет. Работа по развитию юнармейского движения, проделанная в 2017 году, позволила нашей республике занять лидирующие позиции в этом направлении. В Центральном военном округе Башкортостан находится по развитию юнармейского движения в четверке лучших субъектов&raquo;, &mdash; рассказал начальник регионального штаба &laquo;Юнармии&raquo;, член Молодежной палаты при Госсобрании &mdash; Курултае РБ Арсен Зинатуллин.</p>\r\n\r\n<p style="text-align:justify">На заседании было принято решение, в частности, рекомендовать муниципалитетом республики совместно с Военным комиссариатом РБ, Региональным отделением ДОСААФ РБ, Региональным штабом &laquo;Юнармии&raquo; создать зональные советы начальников штабов &laquo;Юнармии&raquo; для координации деятельности на местах, проведения зональных мероприятий, рассмотреть возможность выделения целевых помещений для оборудования юнармейских комнат, организовать популяризацию движения в социальных сетях.</p>\r\n'),
(153, 1513356270, 3, 'Студенческий театр эстрадных миниатюр', '<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://uksivt.ru/?r=news/select&amp;id=153"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1viXYuvM5HS18vt21Gxm-csvHCmLQfp6o" style="float:left; height:201px; width:300px" /></a></span></span></span></span>13 декабря 2017 года в актовом зале Уфимского колледжа статистики, информатики и вычислительной техники театральный коллектив &ldquo;Октава&rdquo; под руководством Гафаровой Ирины Зуфаровны традиционно представил новогодний спектакль. В основу спектакля &laquo;Новый год по Шекспиру&raquo; легла трагедия Уильяма Шекспира &laquo;Ромео и Джульетта&raquo;. Но, в отличие от трагедии, спектакль закончился на позитивной ноте, хотя, не обошлось и без вмешательства злых сил. Не была забыта и предстоящая зимняя олимпиада.</p>\r\n', '<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1viXYuvM5HS18vt21Gxm-csvHCmLQfp6o" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1viXYuvM5HS18vt21Gxm-csvHCmLQfp6o" style="float:left; height:201px; width:300px" /></a></span></span></span></span>13 декабря 2017 года в актовом зале Уфимского колледжа статистики, информатики и вычислительной техники театральный коллектив &ldquo;Октава&rdquo; под руководством Гафаровой Ирины Зуфаровны традиционно представил новогодний спектакль. В основу спектакля &laquo;Новый год по Шекспиру&raquo; легла трагедия Уильяма Шекспира &laquo;Ромео и Джульетта&raquo;. Но, в отличие от трагедии, спектакль закончился на позитивной ноте, хотя, не обошлось и без вмешательства злых сил. Не была забыта и предстоящая зимняя олимпиада.</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1VEaNGfZ7jz9sSjsvZ2BGuICJyntSUAT6" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1VEaNGfZ7jz9sSjsvZ2BGuICJyntSUAT6" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1VjyUmAYc_jRM81Tx2HIgLCbMuVN-QdYo" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1VjyUmAYc_jRM81Tx2HIgLCbMuVN-QdYo" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1DoreVEcgkXdy5mu9p0ZOqMvICloN-szb" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1DoreVEcgkXdy5mu9p0ZOqMvICloN-szb" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black">Подготовка к спектаклю была не из лёгких, однако каждый участник выкладывался на все 100%: вживался в роль, подбирал костюм, реквизиты, учил слова. Выступающих не смутило то, что зал не был полон. Герои старались передать суть постановки: НОВЫЙ ГОД &mdash; время романтики и волшебства; вражда способна погубить мир; любовь торжествует!</span></span></span></span></p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1K-z8-jsx6_Lk1MiATIDRZZIPkpgPSSPS" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1K-z8-jsx6_Lk1MiATIDRZZIPkpgPSSPS" style="float:left; height:298px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1yYEekUgWc3chsRkGn9uoAp-8zlbtWBSO" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1yYEekUgWc3chsRkGn9uoAp-8zlbtWBSO" style="float:left; height:298px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1qUQdfgUQ_i_sWdzgdpJ03waWUPpEh1tf"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1qUQdfgUQ_i_sWdzgdpJ03waWUPpEh1tf" style="float:left; height:298px; width:200px" /></a></span></span></span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black">Представление сопровождалось песнями, танцами, видео. А веселая игра с Дедом Морозом для зрителей и участников спектакля объединила всех, находящихся в зале.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black">&nbsp;Как оказалось, актерам и танцорам удалось создать праздничную новогоднюю атмосферу и удивить своими талантами. Зрителям так понравилось выступление, что они провожали выступающих громкими овациями и улыбками.</span></span></span></span></p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1gssmlVVsJ-L7brL6Yv5NS-rCZyhLJCtM"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1gssmlVVsJ-L7brL6Yv5NS-rCZyhLJCtM" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1hILTW6vfjmTbkNYmA9h9rtcGagvYNy7k" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1hILTW6vfjmTbkNYmA9h9rtcGagvYNy7k" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n			<td>&nbsp;</td>\r\n			<td><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black"><a href="http://drive.google.com/uc?export=view&amp;id=1TLXWtm0UulHeCrogu2YR-StcVyGjRXQ9" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1TLXWtm0UulHeCrogu2YR-StcVyGjRXQ9" style="float:left; height:134px; width:200px" /></a></span></span></span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black">Кроме коллектива &laquo;Октава&raquo; в спектакле участвовали Зайнуллина Алина (1З-1) и танцевальная группа девушек (1ПО-1).</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:10.0pt"><span style="background-color:white"><span style="font-family:Roboto"><span style="color:black">Мингазова Элизабетт(2П-2), Брижатов Стилиан (2П-2), Санникова Ирина (2ПО-1БГУ), Ижбаева Азалия (3П-2).</span></span></span></span></p>\r\n'),
(154, 1513357274, 3, 'Фабрики звезд УКСИВТ-2017', '<p style="text-align:justify">15 декабря на сцене колледжа прошел Гала-концерт &quot;Фабрики звезд УКСИВТ-2017&quot;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:600px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=1EezOvewH-0zJJ8rQaUouFIEfqs7h-UnB" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1EezOvewH-0zJJ8rQaUouFIEfqs7h-UnB" style="height:134px; width:200px" /></a></td>\r\n			<td style="text-align:center">&nbsp;</td>\r\n			<td style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=1Dc0HtyW4mTRPU626CzWstyjQz8W7k6Fw" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1Dc0HtyW4mTRPU626CzWstyjQz8W7k6Fw" style="height:134px; width:200px" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center">&nbsp;</td>\r\n			<td style="text-align:center">&nbsp;</td>\r\n			<td style="text-align:center">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=1trW18_ufzjEZNE9_zi0Qa6X7FUexc8_8" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;"><img alt="" src="http://drive.google.com/uc?export=view&amp;id=1trW18_ufzjEZNE9_zi0Qa6X7FUexc8_8" style="height:298px; width:200px" /></a></td>\r\n			<td style="text-align:center">&nbsp;</td>\r\n			<td style="text-align:center"><a href="http://drive.google.com/uc?export=view&amp;id=1Br7V9R4dRB_bp6igQZDbUaBV4oYU4DDu" onclick="window.open(this.href, '''', '